  let magic = 
    match kind with 
    | Ml -> Config.ast_impl_magic_number
    | Mli -> Config.ast_intf_magic_number in
  let oc = open_out output in 
  output_string oc magic ;
  output_value oc fname;
  output_value oc pt;
  close_out oc 

